    //同步二级类目图标
    private static void syncOptImg(List<String> data){
        data.stream().forEach(j->{
            String sql = "update sm_opt set img = ?,sort = ? where pdd_opt_id = ?";
            final Integer[] i = {0};
            JSON.parseObject(j).getJSONObject("data").getJSONArray("opt2_list").stream().forEach(o->{
                JSONObject json = (JSONObject)o;
                try {
                    mysql.update(sql,json.getString("image_url"), i[0]++,json.getInteger("id"));
                } catch (SQLException e) {
                    e.printStackTrace();
                }
            });
        });

    }

    /**
     * 主类目排序
     */
    private static void mainOptSort(String data){
        String sql = "select id from sm_opt where pdd_opt_id = ?";
        List<String> list = JSON.parseArray(data).stream().map(o -> {
            JSONObject json = (JSONObject) o;
            RowSet rs = null;
            try {
                rs = mysql.query(sql,json.getString("optID"));
                if(rs != null && rs.next())
                    return rs.getString("id");
            } catch (SQLException e) {
                e.printStackTrace();
            }
            return null;
        }).collect(Collectors.toList());
        System.out.println(String.join(",",list));
    }

